"use client";

import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Progress } from "@/components/ui/progress";
import { Button } from "@/components/ui/button";
import { Zap, Loader2, ArrowRight, Flame, Trophy, Target, TrendingUp, Calendar, Shield, Info, Activity } from "lucide-react";
import { Habit, HabitLog } from "@/lib/types";
import { createClient } from "@/lib/supabase/client";
import { useToast } from "@/components/ui/use-toast";
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from "@/components/ui/dialog"

export default function EnhancedHabitsPage() {
    const [habits, setHabits] = useState<Habit[]>([]);
    const [todayLogs, setTodayLogs] = useState<string[]>([]);
    const [allLogs, setAllLogs] = useState<HabitLog[]>([]);
    const [loading, setLoading] = useState(true);
    const [streaks, setStreaks] = useState<Record<string, number>>({});
    const [recoveryHabit, setRecoveryHabit] = useState<string | null>(null);
    const [recoveryDate, setRecoveryDate] = useState(new Date().toISOString().split('T')[0]);

    const supabase = createClient();
    const { toast } = useToast();

    useEffect(() => {
        fetchData();
    }, []);

    async function fetchData() {
        const [habitsRes, logsRes, allLogsRes] = await Promise.all([
            fetch("/api/habits"),
            supabase.from("habit_logs").select("*").eq("completed_at", new Date().toISOString().split('T')[0]),
            supabase.from("habit_logs").select("*").order("completed_at", { ascending: false })
        ]);

        if (habitsRes.ok) {
            const habitsData = await habitsRes.json();
            setHabits(habitsData);
        }

        if (logsRes.data) {
            setTodayLogs(logsRes.data.map((l: any) => l.habit_id));
        }

        if (allLogsRes.data) {
            setAllLogs(allLogsRes.data);
            calculateStreaks(allLogsRes.data);
        }

        setLoading(false);
    }

    function calculateStreaks(logs: HabitLog[]) {
        const streakMap: Record<string, number> = {};

        habits.forEach(habit => {
            const habitLogs = logs
                .filter(log => log.habit_id === habit.id)
                .map(log => log.completed_at)
                .sort()
                .reverse();

            let streak = 0;
            let checkDate = new Date();

            // Handle current streak calculation
            for (let i = 0; i < habitLogs.length; i++) {
                const logDate = habitLogs[i];
                const expectedDate = checkDate.toISOString().split('T')[0];

                if (logDate === expectedDate) {
                    streak++;
                    checkDate.setDate(checkDate.getDate() - 1);
                } else if (logDate < expectedDate) {
                    // Missed a day
                    break;
                }
            }

            streakMap[habit.id] = streak;
        });

        setStreaks(streakMap);
    }

    async function toggleHabit(habitId: string, currentStatus: boolean) {
        const newStatus = !currentStatus;
        if (newStatus) {
            setTodayLogs([...todayLogs, habitId]);
        } else {
            setTodayLogs(todayLogs.filter(id => id !== habitId));
        }

        await fetch("/api/habit-log", {
            method: "POST",
            body: JSON.stringify({
                habit_id: habitId,
                date: new Date().toISOString().split('T')[0],
                completed: newStatus
            })
        });

        if (newStatus) {
            toast({
                title: "Protocol Success! ≡ƒÄ»",
                description: "Habit verified and logged in secure registry",
                variant: "success",
            });
        }

        fetchData();
    }

    async function recoverStreak(habitId: string, date: string) {
        await fetch("/api/habit-log", {
            method: "POST",
            body: JSON.stringify({
                habit_id: habitId,
                date: date,
                completed: true
            })
        });

        toast({
            title: "Streak Recovered",
            description: `Past record for ${date} has been updated.`,
            variant: "success",
        });

        setRecoveryHabit(null);
        fetchData();
    }

    const completionRate = habits.length > 0
        ? Math.round((todayLogs.length / habits.length) * 100)
        : 0;

    const totalStreakDays = Object.values(streaks).reduce((sum, val) => sum + val, 0);
    const longestStreak = Math.max(...Object.values(streaks), 0);

    return (
        <div className="space-y-8 max-w-7xl mx-auto animate-in fade-in slide-in-from-bottom-4 duration-700">
            {/* Header */}
            <div className="flex flex-col md:flex-row md:items-end justify-between gap-6 pb-6 border-b border-white/5">
                <div className="space-y-2">
                    <div className="flex items-center gap-2 mb-1">
                        <Activity className="h-5 w-5 text-primary" />
                        <span className="text-[10px] font-mono uppercase tracking-[0.3em] text-primary/60">Bio-Protocol Module</span>
                    </div>
                    <h1 className="text-4xl font-black tracking-tight text-white flex items-baseline gap-3">
                        DAILY PROTOCOLS <span className="text-sm font-mono text-primary/40">[{habits.length} ACTIVE]</span>
                    </h1>
                </div>
            </div>

            {/* Stats Overview */}
            <div className="grid gap-6 md:grid-cols-4">
                {[
                    { label: "Daily Completion", value: `${completionRate}%`, sub: `${todayLogs.length}/${habits.length} Active`, icon: Target, color: "text-primary", bar: true },
                    { label: "Cumulative Energy", value: totalStreakDays, sub: "Total Streak Days", icon: Flame, color: "text-orange-500" },
                    { label: "Elite Streak", value: longestStreak, sub: "Longest Record", icon: Trophy, color: "text-yellow-500" },
                    { label: "System Sync", value: "98.2%", sub: "Reliability score", icon: TrendingUp, color: "text-blue-500" },
                ].map((stat, i) => (
                    <Card key={i} className="glass border-white/5 bg-black/20 hover:border-primary/20 transition-all duration-500 group">
                        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-3">
                            <span className="text-[10px] font-mono uppercase tracking-[0.2em] text-muted-foreground/60 group-hover:text-muted-foreground transition-colors">
                                {stat.label}
                            </span>
                            <stat.icon className={cn("h-4 w-4 transition-transform group-hover:scale-110", stat.color)} />
                        </CardHeader>
                        <CardContent>
                            <div className={cn("text-3xl font-black tracking-tighter", i === 0 || i === 3 ? "gradient-text" : stat.color)}>
                                {stat.value}
                            </div>
                            <p className="text-[10px] text-muted-foreground mt-1 font-mono uppercase tracking-wider">
                                {stat.sub}
                            </p>
                            {stat.bar && (
                                <div className="mt-4 h-1 w-full bg-white/5 rounded-full overflow-hidden">
                                    <div
                                        className="h-full bg-primary transition-all duration-1000 ease-out"
                                        style={{ width: `${completionRate}%` }}
                                    />
                                </div>
                            )}
                        </CardContent>
                    </Card>
                ))}
            </div>

            {loading ? (
                <div className="h-64 flex items-center justify-center">
                    <Loader2 className="h-8 w-8 text-primary animate-spin" />
                </div>
            ) : habits.length === 0 ? (
                <Card className="glass border-dashed border-primary/20 bg-primary/5">
                    <CardContent className="p-20 text-center">
                        <Zap className="h-16 w-16 text-primary mx-auto mb-6 animate-pulse" />
                        <h3 className="text-2xl font-black mb-2 text-white uppercase tracking-tighter">System Protocols Uninitialized</h3>
                        <p className="text-muted-foreground mb-10 font-mono text-xs uppercase tracking-widest">Connect to neural network to load standard operating protocols.</p>
                        <Button
                            onClick={async () => {
                                setLoading(true);
                                await fetch("/api/habits/init", { method: "POST" });
                                fetchData();
                            }}
                            className="bg-primary text-black hover:bg-primary/90 glow-active px-10 h-14 font-black uppercase tracking-[0.2em]"
                        >
                            <ArrowRight className="mr-2 h-5 w-5" />
                            Initialize Bio-Stream
                        </Button>
                    </CardContent>
                </Card>
            ) : (
                <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
                    {habits.map((habit) => {
                        const isCompleted = todayLogs.includes(habit.id);
                        const streak = streaks[habit.id] || 0;

                        return (
                            <Card
                                key={habit.id}
                                className={cn(
                                    "glass border-white/5 transition-all duration-500 relative overflow-hidden group",
                                    isCompleted ? "bg-primary/5 border-primary/20 shadow-[0_0_30px_rgba(var(--primary),0.05)]" : "hover:border-white/20"
                                )}
                            >
                                <div className={cn(
                                    "absolute top-0 right-0 w-24 h-24 -mr-8 -mt-8 opacity-5 transition-all duration-700 group-hover:scale-125",
                                    isCompleted ? "text-primary" : "text-white"
                                )}>
                                    <Zap className="w-full h-full" />
                                </div>

                                <CardHeader className="pb-4 relative z-10">
                                    <div className="flex items-start justify-between">
                                        <div className="space-y-1">
                                            <h3 className={cn(
                                                "text-lg font-black tracking-tight uppercase transition-colors",
                                                isCompleted ? "text-primary" : "text-white"
                                            )}>
                                                {habit.title}
                                            </h3>
                                            <p className="text-[10px] font-mono text-muted-foreground/60 uppercase tracking-wider line-clamp-1">
                                                {habit.description || "Active Neural Protocol"}
                                            </p>
                                        </div>
                                        <Checkbox
                                            checked={isCompleted}
                                            onCheckedChange={() => toggleHabit(habit.id, isCompleted)}
                                            className="h-6 w-6 border-white/10 data-[state=checked]:bg-primary data-[state=checked]:border-primary transition-all duration-300 shadow-[0_0_10px_transparent] data-[state=checked]:shadow-primary/50"
                                        />
                                    </div>
                                </CardHeader>

                                <CardContent className="space-y-6 relative z-10">
                                    <div className="flex items-center justify-between bg-black/40 p-4 rounded-xl border border-white/5">
                                        <div className="space-y-0.5">
                                            <span className="text-[8px] font-mono text-muted-foreground uppercase tracking-widest">Current Multiplier</span>
                                            <div className="flex items-baseline gap-2">
                                                <span className={cn("text-3xl font-black italic tracking-tighter", streak > 0 ? "text-primary" : "text-white/20")}>
                                                    x{streak}
                                                </span>
                                                <span className="text-[10px] font-mono text-muted-foreground/40 uppercase">Day Streak</span>
                                            </div>
                                        </div>
                                        <div className="flex flex-col items-end gap-1">
                                            {streak >= 7 && (
                                                <Badge variant="outline" className="text-[8px] border-yellow-500/30 text-yellow-500 bg-yellow-500/5 font-mono uppercase px-1.5 h-4">
                                                    Elite Class
                                                </Badge>
                                            )}
                                            <div className="flex gap-1">
                                                {[...Array(7)].map((_, i) => (
                                                    <div
                                                        key={i}
                                                        className={cn(
                                                            "h-1.5 w-1.5 rounded-full transition-all duration-500",
                                                            i < (streak % 7) ? "bg-primary shadow-[0_0_5px_var(--primary)]" : "bg-white/10"
                                                        )}
                                                    />
                                                ))}
                                            </div>
                                        </div>
                                    </div>

                                    <div className="flex gap-2">
                                        <Button
                                            variant="outline"
                                            size="sm"
                                            className="flex-1 h-9 rounded-lg border-white/5 bg-white/5 text-[10px] font-mono uppercase tracking-widest hover:bg-white/10"
                                            onClick={() => {
                                                setRecoveryHabit(habit.id);
                                                setRecoveryDate(new Date(Date.now() - 86400000).toISOString().split('T')[0]);
                                            }}
                                        >
                                            <Shield className="h-3 w-3 mr-2 text-primary" />
                                            Recover Past
                                        </Button>
                                        <Button
                                            variant="outline"
                                            size="sm"
                                            className="h-9 w-9 p-0 border-white/5 bg-white/5 hover:bg-white/10"
                                        >
                                            <Info className="h-3.5 w-3.5 text-muted-foreground/40" />
                                        </Button>
                                    </div>
                                </CardContent>
                            </Card>
                        );
                    })}
                </div>
            )}

            {/* Streak Recovery Dialog */}
            <Dialog open={!!recoveryHabit} onOpenChange={() => setRecoveryHabit(null)}>
                <DialogContent className="glass border-primary/20 bg-black/90 sm:max-w-md">
                    <DialogHeader>
                        <DialogTitle className="text-2xl font-black uppercase tracking-tight text-white flex items-center gap-2">
                            <Shield className="h-6 w-6 text-primary" />
                            Streak Recovery Mode
                        </DialogTitle>
                        <DialogDescription className="font-mono text-xs uppercase tracking-widest text-muted-foreground pt-2">
                            Emergency protocol to restore neural links.
                        </DialogDescription>
                    </DialogHeader>
                    <div className="py-6 space-y-4">
                        <div className="space-y-2">
                            <label className="text-[10px] font-mono text-muted-foreground uppercase tracking-widest ml-1">Protocol Segment (Date)</label>
                            <input
                                type="date"
                                className="w-full bg-white/5 border border-white/10 rounded-xl h-12 px-4 font-mono text-sm text-white focus:border-primary/50 outline-none transition-all"
                                value={recoveryDate}
                                onChange={(e) => setRecoveryDate(e.target.value)}
                            />
                        </div>
                        <div className="p-4 rounded-xl bg-primary/5 border border-primary/20 flex gap-4">
                            <Info className="h-5 w-5 text-primary shrink-0" />
                            <p className="text-[10px] font-mono text-primary/80 uppercase leading-relaxed">
                                Warning: Recovery protocols should only be initialized if a biological link was actually established but not recorded.
                            </p>
                        </div>
                    </div>
                    <div className="flex gap-3">
                        <Button
                            variant="ghost"
                            className="flex-1 h-12 uppercase font-black text-xs tracking-widest text-muted-foreground hover:bg-white/5"
                            onClick={() => setRecoveryHabit(null)}
                        >
                            Cancel
                        </Button>
                        <Button
                            className="flex-1 h-12 bg-primary text-black font-black uppercase text-xs tracking-widest hover:glow-active"
                            onClick={() => recoveryHabit && recoverStreak(recoveryHabit, recoveryDate)}
                        >
                            Restore Link
                        </Button>
                    </div>
                </DialogContent>
            </Dialog>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Slider } from "@/components/ui/slider";
import { createClient } from "@/lib/supabase/client";
import { Review, HabitLog, Habit } from "@/lib/types";
import {
    Loader2,
    Save,
    Brain,
    Sparkles,
    History,
    TrendingUp,
    Activity,
    ShieldAlert,
    ChevronRight,
    MessageSquareQuote,
    BarChart
} from "lucide-react";
import { analyzeHabitPatterns } from "@/lib/ai";
import { useToast } from "@/components/ui/use-toast";
import { cn } from "@/lib/utils";
import { Badge } from "@/components/ui/badge";
import { ResponsiveContainer, BarChart as ReBarChart, Bar, XAxis, YAxis, Tooltip, Cell } from "recharts";

export default function EnhancedReviewsPage() {
    const [reviews, setReviews] = useState<Review[]>([]);
    const [content, setContent] = useState("");
    const [rating, setRating] = useState([7]);
    const [loading, setLoading] = useState(false);
    const [aiInsight, setAiInsight] = useState<any>(null);
    const [isSyncing, setIsSyncing] = useState(true);
    const [chartData, setChartData] = useState<any[]>([]);

    const supabase = createClient();
    const { toast } = useToast();

    useEffect(() => {
        const loadInitialData = async () => {
            setIsSyncing(true);
            await Promise.all([fetchReviews(), fetchHabitStats()]);
            setIsSyncing(false);
        };
        loadInitialData();
    }, []);

    async function fetchReviews() {
        try {
            const res = await fetch("/api/reviews");
            if (res.ok) {
                const data = await res.json();
                setReviews(data);

                // Set chart data based on last 7 reviews
                const last7 = data.slice(0, 7).reverse();
                setChartData(last7.map((r: any) => ({
                    date: new Date(r.date).toLocaleDateString('en-US', { weekday: 'short' }),
                    rating: r.rating || 0
                })));
            }
        } catch (error) {
            console.error("Fetch reviews error:", error);
        }
    }

    async function fetchHabitStats() {
        try {
            const [logsRes, habitsRes] = await Promise.all([
                supabase.from("habit_logs").select("*").order("completed_at", { ascending: false }).limit(100),
                supabase.from("habits").select("*")
            ]);

            if (logsRes.data && habitsRes.data) {
                const insights = await analyzeHabitPatterns(logsRes.data.map(l => ({
                    date: l.completed_at,
                    habit_id: l.habit_id
                })));
                setAiInsight(insights);
            }
        } catch (error) {
            console.error("Habit stats error:", error);
        }
    }

    async function submitReview() {
        if (!content.trim()) {
            toast({
                title: "Incomplete Signal",
                description: "Feedback buffer empty. Please provide reflection data.",
                variant: "destructive",
            });
            return;
        }

        setLoading(true);
        try {
            const res = await fetch("/api/reviews", {
                method: "POST",
                body: JSON.stringify({
                    content,
                    rating: rating[0],
                    date: new Date().toISOString().split('T')[0]
                })
            });

            if (res.ok) {
                const newReview = await res.json();
                setReviews([newReview, ...reviews]);
                setContent("");
                setRating([7]);

                toast({
                    title: "Signal Synchronized",
                    description: "Neural reflection data cached successfully.",
                    variant: "success",
                });

                fetchReviews(); // Refresh chart
                fetchHabitStats(); // Refresh AI
            }
        } catch (error) {
            toast({
                title: "Sync Error",
                description: "Failed to transmit neural reflection. System timeout.",
                variant: "destructive",
            });
        } finally {
            setLoading(false);
        }
    }

    const getRatingColor = (val: number) => {
        if (val >= 8) return "text-primary";
        if (val >= 5) return "text-yellow-500";
        return "text-red-500";
    };

    return (
        <div className="space-y-10 max-w-6xl mx-auto animate-in fade-in slide-in-from-bottom-6 duration-1000 p-4 md:p-8">
            {/* Header HUD */}
            <div className="flex flex-col md:flex-row justify-between items-start md:items-end gap-6 pb-6 border-b border-white/5">
                <div className="space-y-2">
                    <div className="flex items-center gap-2 mb-1">
                        <Brain className="h-5 w-5 text-primary" />
                        <span className="text-[10px] font-mono uppercase tracking-[0.3em] text-primary/60">Neural Feedback Module</span>
                    </div>
                    <h1 className="text-4xl font-black tracking-tight text-white uppercase italic">
                        SYSTEM <span className="gradient-text">REVIEW</span>
                    </h1>
                    <p className="text-xs font-mono text-muted-foreground uppercase tracking-widest">Cache daily reflection. Optimize biological performance.</p>
                </div>
                <div className="flex items-center gap-4 bg-white/5 p-4 rounded-xl border border-white/10">
                    <Activity className="h-5 w-5 text-primary animate-pulse" />
                    <div className="text-right">
                        <div className="text-[9px] font-mono text-muted-foreground uppercase">Memory Density</div>
                        <div className="text-sm font-black text-white">{reviews.length} SEGMENTS</div>
                    </div>
                </div>
            </div>

            <div className="grid gap-10 lg:grid-cols-12">
                {/* Left Column: Log Entry & Chart */}
                <div className="lg:col-span-7 space-y-8">
                    <Card className="glass border-primary/20 bg-primary/5 shadow-[0_0_40px_rgba(var(--primary),0.02)] overflow-hidden">
                        <div className="absolute top-0 right-0 p-6 opacity-5">
                            <MessageSquareQuote className="h-32 w-32 text-primary" />
                        </div>
                        <CardHeader>
                            <CardTitle className="text-xl font-black uppercase text-white tracking-tight flex items-center gap-3">
                                <div className="p-2 rounded-lg bg-primary/10 border border-primary/20">
                                    <Sparkles className="h-4 w-4 text-primary" />
                                </div>
                                Initialize Reflection
                            </CardTitle>
                            <CardDescription className="font-mono text-[10px] uppercase tracking-widest">Input core metrics for current temporal cycle</CardDescription>
                        </CardHeader>
                        <CardContent className="space-y-8 relative z-10">
                            <div className="space-y-4">
                                <div className="flex items-center justify-between px-1">
                                    <label className="text-[10px] font-mono uppercase tracking-widest text-muted-foreground">Productivity Vector (1 - 10)</label>
                                    <span className={cn("text-2xl font-black italic tracking-tighter", getRatingColor(rating[0]))}>
                                        {rating[0]}.0
                                    </span>
                                </div>
                                <Slider
                                    value={rating}
                                    onValueChange={setRating}
                                    max={10}
                                    min={1}
                                    step={1}
                                    className="py-4"
                                />
                            </div>

                            <div className="space-y-3">
                                <label className="text-[10px] font-mono uppercase tracking-widest text-muted-foreground px-1">Neural Narrative (Reflection)</label>
                                <Textarea
                                    placeholder="SYSTEM LOG: What operational upgrades occurred? What faults were detected?..."
                                    className="bg-black/60 border-white/5 focus:border-primary/40 rounded-2xl p-6 font-mono text-sm min-h-[160px]"
                                    value={content}
                                    onChange={(e) => setContent(e.target.value)}
                                />
                            </div>

                            <Button
                                onClick={submitReview}
                                disabled={loading}
                                className="w-full h-14 bg-primary text-black font-black uppercase tracking-[0.2em] hover:glow-active transition-all"
                            >
                                {loading ? (
                                    <Loader2 className="h-5 w-5 animate-spin" />
                                ) : (
                                    <>
                                        <Save className="h-5 w-5 mr-3" />
                                        Commit to Cache
                                    </>
                                )}
                            </Button>
                        </CardContent>
                    </Card>

                    {/* Historical Pulse Chart */}
                    <Card className="glass border-white/5 bg-black/40">
                        <CardHeader className="pb-2">
                            <CardTitle className="text-sm font-mono uppercase tracking-[0.3em] flex items-center gap-2 text-muted-foreground">
                                <BarChart className="h-4 w-4" />
                                Efficiency Resonance
                            </CardTitle>
                        </CardHeader>
                        <CardContent className="h-[200px] w-full mt-4">
                            {chartData.length > 0 ? (
                                <ResponsiveContainer width="100%" height="100%">
                                    <ReBarChart data={chartData}>
                                        <XAxis
                                            dataKey="date"
                                            axisLine={false}
                                            tickLine={false}
                                            tick={{ fill: '#666', fontSize: 10, fontFamily: 'monospace' }}
                                        />
                                        <Tooltip
                                            cursor={{ fill: 'rgba(255,255,255,0.05)' }}
                                            contentStyle={{
                                                backgroundColor: '#000',
                                                border: '1px solid rgba(255,255,255,0.1)',
                                                borderRadius: '8px',
                                                fontSize: '10px'
                                            }}
                                        />
                                        <Bar dataKey="rating" radius={[4, 4, 0, 0]}>
                                            {chartData.map((entry, index) => (
                                                <Cell key={`cell-${index}`} fill={entry.rating >= 8 ? "hsl(var(--primary))" : entry.rating >= 5 ? "#eab308" : "#ef4444"} fillOpacity={0.4} />
                                            ))}
                                        </Bar>
                                    </ReBarChart>
                                </ResponsiveContainer>
                            ) : (
                                <div className="h-full flex items-center justify-center text-[10px] font-mono text-muted-foreground/30 uppercase tracking-[0.2em]">Insufficient data for rendering pulse</div>
                            )}
                        </CardContent>
                    </Card>
                </div>

                {/* Right Column: AI Insights & History */}
                <div className="lg:col-span-5 space-y-8">
                    {/* AI Insight HUD */}
                    <Card className="glass border-primary/20 bg-black/40 overflow-hidden relative">
                        <div className="absolute top-0 left-0 w-full h-1 bg-primary/20" />
                        <CardHeader className="pb-4">
                            <CardTitle className="text-sm font-mono uppercase tracking-[0.3em] flex items-center gap-2 text-primary">
                                <div className="h-1.5 w-1.5 rounded-full bg-primary animate-ping" />
                                Neural Signal Analysis
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            {isSyncing ? (
                                <div className="py-10 flex flex-col items-center gap-4">
                                    <Loader2 className="h-6 w-6 animate-spin text-primary/40" />
                                    <span className="text-[8px] font-mono text-muted-foreground uppercase tracking-widest animate-pulse">Filtering Noise...</span>
                                </div>
                            ) : aiInsight ? (
                                <div className="space-y-6">
                                    <div className="flex items-center justify-between bg-white/5 p-4 rounded-xl border border-white/5">
                                        <div className="space-y-0.5">
                                            <span className="text-[8px] font-mono text-muted-foreground uppercase opacity-40">System Trend</span>
                                            <div className="flex items-center gap-2">
                                                <TrendingUp className="h-3.5 w-3.5 text-primary" />
                                                <span className="text-sm font-black text-white uppercase italic">{aiInsight.trend}</span>
                                            </div>
                                        </div>
                                        <Badge variant="outline" className="border-primary/20 text-primary uppercase text-[8px] font-mono">Stable Link</Badge>
                                    </div>

                                    <div className="space-y-4">
                                        <p className="text-xs font-mono text-muted-foreground leading-relaxed uppercase">
                                            {aiInsight.insight}
                                        </p>
                                        <div className="p-4 rounded-xl bg-primary/5 border border-primary/20 relative group overflow-hidden">
                                            <div className="absolute top-0 right-0 p-3 opacity-10 group-hover:opacity-20 transition-opacity">
                                                <ShieldAlert className="h-8 w-8 text-primary" />
                                            </div>
                                            <span className="text-[8px] font-mono text-primary uppercase tracking-[0.2em] block mb-2 font-black">CORE DIRECTIVE:</span>
                                            <p className="text-sm font-bold text-white relative z-10 italic">
                                                &quot;{aiInsight.recommendation}&quot;
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            ) : (
                                <div className="p-10 text-center">
                                    <p className="text-[10px] font-mono text-muted-foreground/30 uppercase tracking-widest">No analytic data available.</p>
                                </div>
                            )}
                        </CardContent>
                    </Card>

                    {/* Timeline / History */}
                    <div className="space-y-6">
                        <h3 className="text-[10px] font-black uppercase text-muted-foreground tracking-[0.4em] flex items-center gap-3">
                            <History className="h-4 w-4" />
                            Signal History
                        </h3>
                        <div className="space-y-4">
                            {reviews.length === 0 ? (
                                <div className="p-10 border border-dashed border-white/5 rounded-2xl text-center">
                                    <p className="text-[10px] font-mono text-muted-foreground/20 uppercase tracking-widest">Historical Cache Empty</p>
                                </div>
                            ) : (
                                reviews.slice(0, 5).map((review) => (
                                    <Card key={review.id} className="glass border-white/5 bg-white/5 transition-all hover:border-white/15 hover:bg-white/[0.07] group cursor-pointer overflow-hidden">
                                        <div className="absolute left-0 top-0 bottom-0 w-1 bg-transparent group-hover:bg-primary/40 transition-all" />
                                        <CardContent className="p-5">
                                            <div className="flex items-center justify-between mb-4">
                                                <div className="flex items-center gap-3">
                                                    <div className="text-[9px] font-mono text-muted-foreground/40 uppercase bg-white/5 px-2 py-1 rounded border border-white/5">
                                                        {review.date}
                                                    </div>
                                                </div>
                                                <div className={cn("text-lg font-black italic tracking-tighter", getRatingColor(review.rating || 0))}>
                                                    {review.rating || 0}.0
                                                </div>
                                            </div>
                                            <p className="text-xs text-foreground/70 font-mono line-clamp-2 italic leading-relaxed">
                                                &quot;{review.content}&quot;
                                            </p>
                                            <div className="mt-4 flex justify-end">
                                                <button className="text-[8px] font-mono text-primary/40 uppercase tracking-widest flex items-center gap-1 group-hover:gap-2 transition-all group-hover:text-primary">
                                                    Open Data Leaf <ChevronRight className="h-3 w-3" />
                                                </button>
                                            </div>
                                        </CardContent>
                                    </Card>
                                ))
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

